@extends("home.index")
 @section("content")
<div class="profile-panel" role="main"><!-- ngIf: pageTitleVisible --><h3 ng-if="pageTitleVisible" class="profile-paneltitle ng-scope"><span ng-bind="pageTitle" class="ng-binding">修改支付限额</span> <span class="subtitle ng-binding" ng-bind-html="pageSubtitle | toTrusted"></span></h3><!-- end ngIf: pageTitleVisible --><div class="profile-panelcontent" ng-transclude=""><ol class="instruction-steps ng-isolate-scope" instruction-steps="" steps-data="steps" current-step="currentStep"><!-- ngRepeat: step in steps --><li class="instruction-step ng-scope" ng-class="{'waiting': step.number > current}" ng-repeat="step in steps"><!-- ngIf: step.number >= current --><span class="status ng-scope icon-dot" ng-class="{'icon-dot': step.number === current, 'icon-circle': step.number > current}" ng-if="step.number >= current"><var ng-bind="step.number" class="ng-binding">1</var></span><!-- end ngIf: step.number >= current --> <!-- ngIf: step.number < current --> <span class="step-text ng-binding" ng-bind="step.text">修改每日支付限额</span></li><!-- end ngRepeat: step in steps --><li class="instruction-step ng-scope waiting" ng-class="{'waiting': step.number > current}" ng-repeat="step in steps"><!-- ngIf: step.number >= current --><span class="status ng-scope icon-circle" ng-class="{'icon-dot': step.number === current, 'icon-circle': step.number > current}" ng-if="step.number >= current"><var ng-bind="step.number" class="ng-binding">2</var></span><!-- end ngIf: step.number >= current --> <!-- ngIf: step.number < current --> <span class="step-text ng-binding" ng-bind="step.text">验证手机号码</span></li><!-- end ngRepeat: step in steps --><li class="instruction-step ng-scope waiting" ng-class="{'waiting': step.number > current}" ng-repeat="step in steps"><!-- ngIf: step.number >= current --><span class="status ng-scope icon-circle" ng-class="{'icon-dot': step.number === current, 'icon-circle': step.number > current}" ng-if="step.number >= current"><var ng-bind="step.number" class="ng-binding">3</var></span><!-- end ngIf: step.number >= current --> <!-- ngIf: step.number < current --> <span class="step-text ng-binding" ng-bind="step.text">修改成功</span></li><!-- end ngRepeat: step in steps --></ol><form class="security-service ng-scope ng-pristine ng-valid" ng-show="currentStep === 1" ng-submit="next()" novalidate=""><div class="formfield ng-isolate-scope" ng-class="{ 'validate-error': model.$hintTypes[property] === 'error' }" form-field="" label="支付限额" model="quotaLink" property="quota"><label ng-bind="label" class="ng-binding">支付限额</label><input name="quota" ng-model="quotaLink.quota" placeholder="请输入支付限额" autocomplete="off" class="ng-scope ng-pristine ng-valid"><div class="formfield-hint"><span ng-class="{ 'icon-dot-error': model.$hintTypes[property] === 'error', 'icon-dot-warning': model.$hintTypes[property] === 'warning' }" ng-bind="model.$hints[property]" class="ng-binding"></span></div></div><button type="submit" class="btn-primary btn-lg modifypay-submit">下一步</button></form><form class="security-service ng-scope ng-pristine ng-valid ng-hide" ng-show="currentStep === 2" ng-submit="confirm()" novalidate=""><div class="field-default formfield ng-isolate-scope" ng-class="{ 'validate-error': model.$hintTypes[property] === 'error' }" form-field="" label="手机号码"><label ng-bind="label" class="ng-binding">手机号码</label><span class="field-default-value default-mobile ng-binding ng-scope" ng-bind="user.mobile | mobileMask">151****2791</span><div class="formfield-hint"><span ng-class="{ 'icon-dot-error': model.$hintTypes[property] === 'error', 'icon-dot-warning': model.$hintTypes[property] === 'warning' }" ng-bind="model.$hints[property]" class="ng-binding"></span></div></div><div class="formfield form-field-verifymobile ng-isolate-scope" ng-class="{ 'validate-error': model.$hintTypes[property] === 'error' }" mobile-verify-field="" label="验证码" model="mobileVerifyLink" property="validate_code" link="mobileVerifyLink"><label ng-bind="label" class="ng-binding">验证码</label><input name="validate_code" ng-model="link.validate_code" class="ng-pristine ng-valid"> <button type="button" ng-class="{ 'disabled': audio === 'running' }" countdown="" status="sms" mode="text" time="60" tpl="?time秒 重新发送" started-once-text="重新发送" ng-click="getToken(link.sendType)" class="ng-isolate-scope">获取验证码</button><div class="formfield-hint"><span ng-class="{
      'icon-dot-error': model.$hintTypes[property] === 'error',
      'icon-dot-warning': model.$hintTypes[property] === 'warning',
      'multi': audio === 'running' }" ng-bind="model.$hints[property]" class="ng-binding"></span> <span class="verifymobile-msg" ng-hide="sms === 'running' || sms === 'end' || audio === 'running'">为保障账号安全，请先对你的身份进行验证</span> <span class="verifymobile-msg ng-hide" ng-show="sms === 'running' &amp;&amp; audio !== 'running'">短信已发送，请输入短信中的验证码</span> <span class="verifymobile-msg ng-hide" ng-show="sms === 'end' &amp;&amp; audio !== 'running'"><span>收不到短信？请使用</span> <a href="javascript:" ng-click="getToken('audio')">语音验证码</a></span> <span class="verifymobile-msg multi ng-hide" ng-show="audio === 'running'"><span>电话拨打中... 请留意你的手机来电</span> <span countdown="" status="audio" mode="text" time="60" tpl="(?time秒 可重新拨打)" class="ng-isolate-scope"></span></span></div></div><div class="form-group"><button type="submit" class="btn-primary btn-lg security-submit">确认修改</button></div><div class="form-group"><p class="service-text notice-text">当前号码已不用或丢失/无法收到验证码？请联系客服：<span class="default-mobile">10105757</span></p></div></form><div class="security-service modifypay-success ng-scope ng-hide" ng-show="currentStep === 3"><img src="//faas.elemecdn.com/desktop/media/img/security-success.3730bf.png" alt=""><p><b class="ng-binding">你每日的支付额度已经调整为50元。</b><br>5秒后返回安全中心 <a href="/personal/security" ng-click="back()">立即返回</a></p></div></div></div>

       @endsection